insert into book (book_id, ISBN, title, book_language, abstract, publisher, page_nr) values (91, '978-2473160749', 'The Question Concerning Technology in China : An Essay in Cosmotechnics', 'English', 'A systematic historical survey of Chinese thought is followed by an investigation of the historical-metaphysical questions of modern technology, asking how Chinese thought might contribute to a renewed questioning of globalized technics.', 'Houghton Mifflin Harcourt', '748');
