insert into book (book_id, ISBN, title, book_language, abstract, publisher, page_nr) values (95, '979-2966411488', 'Morecambe and Wise: The Lost Tapes', 'English', 'Only one episode from Series One of The Morecambe and Wise Show still exists in the BBC archive. However, thanks to home recordings made by TV viewers at the time, it is now possible to listen to soundtracks from all seven of the ''lost'' episodes.', 'HarperCollins', '819');
